import "./Settings.css";
import React, { useState } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter, Route, Switch } from "react-router-dom";

function Settings() {
  const options = [
    {
      header: {
        name: "Account",
      },

      values: [],
    },

    {
      header: {
        name: "Application",
      },

      values: [],
    },

    {
      header: {
        name: "Billing",
      },

      values: [
        {
          name: "Billing Information",
          description: "Manage your billing information",
          tags: ["credit cards"],
        },
        {
          name: "Usage",
          description: "View your usage statistics",
          tags: [],
        },
        {
          name: "Invoices",
          description: "Track your invoices and previous purchases",
          tags: [],
        },
        {
          name: "Invoice Address",
          description:
            "We'll this this address on your invoice, if blank, your bill address will be printed instead",
          tags: [],
        },
      ],
    },

    {
      header: {
        name: "Support",
      },

      values: [
        {
          name: "Help",
          description: "Having trouble",
          tags: [],
        },
        {
          name: "FAQ",
          description: "View Frequently Asked Questions",
          tags: [],
        },
      ],
    },
  ];

  const [visibleOptions, setVisibleOptions] = useState(options);

  return (
    <div className="Settings">
      <div className="container my-5">
        <h1>
          <span>
            <button className="btn btn-secondary">
              {" "}
              <span>&lt;</span> Back{" "}
            </button>{" "}
            Settings
          </span>
        </h1>

        <input type="text" className="form-control" placeholder="Search..." />

        <div>
          {visibleOptions.map((option) => (
            <div key={option.header.name}>
              <h3>{option.header.name}</h3>

              <div>
                {option.values.map((value) => (
                  <div key={value.name}>
                    <ul className="list-group">
                      <li className="list-group-item mb-2"></li>
                    </ul>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Settings;
