import styled from 'styled-components';
export const Wrapper = styled.div`
    font-family:Arial;
    h1{
        color:white;
    }
    .settingheader{
        h3{color:white;}
    }   
    .MuiInput-root{
        width:100%;
        background-color:white;
        border-radius:0.3rem;
        padding: 0 1rem;
    }
    ul{
        list-style-type:none;
        margin:0;
    }
    h6{
        font-size:1rem;
        margin:0;
    }
    .group{
        border:1px solid white;
        border-radius:0.3rem;
        padding:1rem 1.5rem;
        margin:0.5rem 0;
        background-color:white;
    }
`;
