import styled from 'styled-components'

export const CaptionWrapper = styled.div`
    p {
        margin-top: -15px;
        padding: 0 5px;
    }
`;

export const FormWrapper = styled.div`
.MuiFilledInput-input {
    padding: 5px 10px;
    color: #fff;
    background-color: rgba(255,255,255,.1);
    border-radius: 5px;
}
`;
